export function twMerge(...classes) {
  const tokens = classes.join(' ').trim().split(/\s+/);
  const result = [];
  const seen = new Set();
  for (let i = tokens.length - 1; i >= 0; i--) {
    const cls = tokens[i];
    const prefix = cls.split('-')[0];
    if (!seen.has(prefix)) {
      result.unshift(cls);
      seen.add(prefix);
    }
  }
  return result.join(' ');
}
export default twMerge;
